mapper.select:
SELECT reserv_no, res_name, reserv_name, reserv_phone, cnt, rdate, managerno, resno, customerno
FROM reservation
WHERE reserv_no = #{reserv_no}

mapper.update:
UPDATE reservation 
SET reserv_name = #{reserv_name}, reserv_phone=#{reserv_phone}, cnt = #{cnt}, rdate=#{rdate}
WHERE reserv_no=#{reserv_no}
