mapper.select:
SELECT supplierno, name, rdate, managerno
FROM supplier
WHERE supplierno = #{supplierno}

mapper.update:
UPDATE supplier 
SET name = #{name}
WHERE supplierno=#{supplierno}

mapper.select:
SELECT COUNT(*) as cnt FROM rescontents
WHERE resno=#{resno}
WHERE resno=#{resno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(rescontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT supplierno, name, rdate, managerno, r
FROM (
  SELECT supplierno, name, rdate, managerno, rownum as r
  FROM (
    SELECT supplierno, name, rdate, managerno
    FROM supplier


        WHERE managerno=#{managerno}


        WHERE managerno=#{managerno} AND (UPPER(name) LIKE '%' || UPPER(#{word}) || '%' )
        ORDER BY supplierno DESC


  )          
)
WHERE  r >= #{start_num} AND r <= #{end_num}
